n class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;10&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;			num_2 &lt;span class=&#34;token operator&#34;&gt;+=&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;11&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;		&lt;span class=&#34;token punctuation&#34;&gt;}&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;12&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;		&lt;span class=&#34;token keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;token function&#34;&gt;pow&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;3&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; num_3&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;*&lt;/span&gt;&lt;span class=&#34;token function&#34;&gt;pow&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; num_2&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;13&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;	&lt;span class=&#34;token punctuation&#34;&gt;}&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;14&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token punctuation&#34;&gt;}&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;class Employee{
public:
	/*Employee(string name, string place, string city, string code)
		: name(name), place(place), city(city), code(code){}*/
	Employee(){}
	~Employee(){}
	void setName(string n){ name = n; }
	void location(string p){ place = p; }
	void City_name(string c){ city = c; }
	void postcode(string d){ code = d; }
	void display();


private:
	string name, place, city, code;

};
void Employee::display(){
	cout &amp;lt;&amp;lt; name &amp;lt;&amp;lt; &#34;\t&#34; &amp;lt;&amp;lt; place &amp;lt;&amp;lt; &#34;\t&#34; &amp;lt;&amp;lt; city &amp;lt;&amp;lt; &#34;\t&#34; &amp;lt;&amp;lt; code;
}
int main()
{

	string name, place, city, code;
	cout &amp;lt;&amp;lt; &#34;请输入你的姓名、地址、城市和邮政编码\n&#34;;
	cin &amp;gt;&amp;gt; name &amp;gt;&amp;gt; place &amp;gt;&amp;gt; city &amp;gt;&amp;gt; code;
	/*Employee e(name,place,city,code);*/
	Employee e;
	e.setName(name);
	e.location(place);
	e.City_name(city);
	e.postcode(code);
	e.display();

	return 0;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;得到的知识：构造函数若无花括号，则只是声明，需具体实现 &lt;code&gt;Employ：：Employ (){}&lt;/code&gt; . 构造函数后的 &lt;code&gt;name(name)&lt;/code&gt;  为初始化&lt;/p&gt;

 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;//在限定时间内计算两点间的距离，并核对
#include&amp;lt;iostream&amp;gt;
#include&amp;lt;cmath&amp;gt;
#include&amp;lt;Windows.h&amp;gt;
#include&amp;lt;time.h&amp;gt;
using namespace std;
class point{
public:
	point(int x, int y, int a, int b)
		:x(x), y(y), a(a), b(b){
		cout &amp;lt;&amp;lt; &#34;求该两点(&#34; &amp;lt;&amp;lt; x &amp;lt;&amp;lt; &#34;,&#34; &amp;lt;&amp;lt; y &amp;lt;&amp;lt; &#34;)&#34; &amp;lt;&amp;lt; &#34;  &#34; &amp;lt;&amp;lt; &#34;(&#34; &amp;lt;&amp;lt; a &amp;lt;&amp;lt; &#34;,&#34; &amp;lt;&amp;lt; b &amp;lt;&amp;lt; &#34;)的距离\n&#34;;
		cout &amp;lt;&amp;lt; &#34;你有4秒钟计算答案，计算机将在4秒后给出答案\n&#34;;
		Sleep(1000);
		cout &amp;lt;&amp;lt; &#34;你还有3秒钟计算答案，计算机将在3秒后给出答案\n&#34;;
		Sleep(1000);
		cout &amp;lt;&amp;lt; &#34;你还有2秒钟计算答案，计算机将在2秒后给出答案\n&#34;;
		Sleep(1000);
		cout &amp;lt;&amp;lt; &#34;你还有1秒钟计算答案，计算机将在1秒后给出答案\n&#34;;
		Sleep(1000);
		cout &amp;lt;&amp;lt; &#34;时间到\n&#34;;

	}
	friend double dist_cal(const point &amp;amp;q);
private:
	int x, y,a,b;

};
double dist_cal(const point &amp;amp;q){

	return  sqrt(abs((q.x - q.a)*(q.x - q.a)) + abs((q.y - q.b)*(q.y - q.b)));
}

int main()
{
	int x, y, a, b;
	int n = 1;
	while (n)
	{

		cout &amp;lt;&amp;lt; &#34;所求的两点的坐标，请计算\n&#34;;
		srand(time(NULL));
		x = rand() % 10;
		y = rand() % 10;
		a = rand() % 10;
		b = rand() % 10;
		point A(x, y, a, b);
		cout &amp;lt;&amp;lt; &#34;距离为&#34; &amp;lt;&amp;lt; dist_cal(A)&amp;lt;&amp;lt;endl&amp;lt;&amp;lt;&#34;请自行核对&#34;&amp;lt;&amp;lt;endl;
		cout &amp;lt;&amp;lt; &#34;请选择是否继续(0为退出，其他为继续)&#34;;
		cin &amp;gt;&amp;gt; n;
	}
	return 0;
}
&lt;/code&gt;&lt;/pre&gt;

 &lt;h1 id=&#34;矩阵创建及转置&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#矩阵创建及转置&#34;&gt;#&lt;/a&gt; 矩阵创建及转置&lt;/h1&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include&amp;lt;iostream&amp;gt;
#include&amp;lt;vector&amp;gt;
#include&amp;lt;iomanip&amp;gt;
using namespace std;

vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt; juzheng_change(vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;&amp;amp;a)
{
	vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;b(a[0].size(), vector&amp;lt;int&amp;gt;(a.size()));
	for (int i = 0; i &amp;lt; a.size(); i++)
	{
		for (int j = 0; j &amp;lt; a[0].size(); j++)
		{
			b[j][b[0].size() - i - 1] = a[i][j];         // 进行数值传递
		}
	}
	return b;
}
int main()
{
	int m, n,num;
	cout &amp;lt;&amp;lt; &#34;请分别输入矩阵的行、列数：&#34;;
	cin &amp;gt;&amp;gt; m &amp;gt;&amp;gt; n;                   // 确定矩阵大小
	vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;a(m,vector&amp;lt;int&amp;gt;(n));
	for (int i = 0; i &amp;lt; m; i++)
	{
		for (int j = 0; j &amp;lt; n; j++)
		{
			cin &amp;gt;&amp;gt; num;
			a[i][j] = num;              // 矩阵内元素确认
		}
	}
	vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;b(n, vector&amp;lt;int&amp;gt;(m));
	b= juzheng_change(a);
	cout &amp;lt;&amp;lt; &#34;转置后的矩阵:&#34; &amp;lt;&amp;lt; endl;
	for (int i = 0; i &amp;lt; n; i++)
	{
		for (int j = 0; j &amp;lt; m; j++)
		{
			cout &amp;lt;&amp;lt; b[i][j] &amp;lt;&amp;lt; &#34;  &#34;;              // 输出
		}
		cout &amp;lt;&amp;lt; endl;
	}
}
&lt;/code&gt;&lt;/pre&gt;
&lt;h1 id=&#34;对齐输出&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#对齐输出&#34;&gt;#&lt;/a&gt; 对齐输出：&lt;/h1&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include&amp;lt;iostream&amp;gt;
#include&amp;lt;vector&amp;gt;
#include&amp;lt;iomanip&amp;gt;
#include&amp;lt;algorithm&amp;gt;
#include&amp;lt;string&amp;gt;
using namespace std;

int cal(int n)
{
	return (to_string(n).length());
}

vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt; juzheng_change(vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;&amp;amp;a)
{
	vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;b(a[0].size(), vector&amp;lt;int&amp;gt;(a.size()));
	for (int i = 0; i &amp;lt; a.size(); i++)
	{
		for (int j = 0; j &amp;lt; a[0].size(); j++)
		{
			b[j][b[0].size() - i - 1] = a[i][j];         // 进行数值传递
		}
	}
	return b;
}
int main()
{
	int m, n;
	int  num,ans;
	cout &amp;lt;&amp;lt; &#34;请分别输入矩阵的行、列数：&#34;;
	cin &amp;gt;&amp;gt; m &amp;gt;&amp;gt; n;                   // 确定矩阵大小
	vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;a(m,vector&amp;lt;int&amp;gt;(n));
	cout &amp;lt;&amp;lt; &#34;输入矩阵各位置的值:&#34; &amp;lt;&amp;lt; endl;
	for (int i = 0; i &amp;lt; m; i++)
	{
		for (int j = 0; j &amp;lt; n; j++)
		{
			cin &amp;gt;&amp;gt; num;
			a[i][j] = num; // 矩阵内元素确认
			ans = max(ans, num);
		}
	}
	vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt;b(n, vector&amp;lt;int&amp;gt;(m));
	b= juzheng_change(a);
	int x = cal(ans);
	cout &amp;lt;&amp;lt; &#34;转置后的矩阵:&#34; &amp;lt;&amp;lt; endl;
	for (int i = 0; i &amp;lt; n; i++)
	{
		for (int j = 0; j &amp;lt; m; j++)
		{
			cout &amp;lt;&amp;lt; setw(x)&amp;lt;&amp;lt;b[i][j]&amp;lt;&amp;lt;&#34; &#34;;              // 输出
		}
		cout &amp;lt;&amp;lt; endl;
	}
}
